import sqlite3

conn = sqlite3.connect('stories.db')
c = conn.cursor()

c.execute('''
CREATE TABLE IF NOT EXISTS stories (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    title TEXT NOT NULL,
    image TEXT
)
''')

